#pragma config(Motor,  port4,           pickup,        tmotorVex393_MC29, openLoop)
#pragma config(Motor,  port5,           track,         tmotorVex393_MC29, openLoop)
#pragma config(Motor,  port6,           shooter,       tmotorVex393_MC29, openLoop)
