/////////////////////////////////////////////////////////////////////////////////////////
//
//                                 Autonomous Task
//
// This task is used to control your robot during the autonomous phase of a VEX Competition.
// You must modify the code to add your own robot specific commands here.
//
/////////////////////////////////////////////////////////////////////////////////////////

task autonomous()
{
	// Drive 24 inches
	motor[leftMotor] = 127;
	motor[rightMotor] = 127;

	// Keep driving at 127 for 1 second
	// TODO: Need to measure how long it takes to reach 24 inches
	wait1Msec(1000);

	// Stop the Robot
	motor[leftMotor] = 0;
	motor[rightMotor] = 0;

	// Turn 45 degrees
	// ESTIMATES:
	// Speed: 80 Time: 100ms Angle: 15
	motor[leftMotor] = 80;
	motor[rightMotor] = 80;

	wait1Msec(300);

	motor[leftMotor] = 0;
	motor[rightMotor] = 0;

	// Drive 23.75 inches
	motor[leftMotor] = 127;
	motor[rightMotor] = 127;

	// Keep driving at 127 for 1 second
	// TODO: Need to measure how long it takes to reach 23.75 inches
	wait1Msec(950);

	// Stop the Robot
	motor[leftMotor] = 0;
	motor[rightMotor] = 0;

	// Pick up balls
	motor[pickup] = 127;
	motor[track] = 127;
	motor[shooter] = 127;
	
	wait1Msec(5000);
	
	// Reset
	motor[pickup] = 0;

}

/////////////////////////////////////////////////////////////////////////////////////////
//
//                                 User Control Task
//
// This task is used to control your robot during the user control phase of a VEX Competition.
// You must modify the code to add your own robot specific commands here.
//
/////////////////////////////////////////////////////////////////////////////////////////

task usercontrol()
{
	while (true)
	{
		// Tank driving
		// Channel 3 = Left motor
		// Channel 2 = Right Motor
	
		motor[leftMotor] = vexRT[Ch3];
		motor[rightMotor] = vexRT[Ch2];
		
		// Ball pickup
		if (vexRT[Btn6U] == 1) {
			motor[pickup] = -127;
		}
		else if (vexRT[Btn6D] == 1) {
			motor[pickup] = 127;
		}
		else {
			motor[pickup] = 0;	
		}
		
		// Track
		if (vexRT[Btn5U] == 1) {
			motor[track] = 127;	
		}
		else if (vexRT[Btn5D] == 1) {
			motor[track] = -127;	
		}
		else {
			motor[track] = 0;
		}
		
		// Shooter
		if (vexRT[Btn8D] == 1) {
			motor[shooter] = 127;
		}
		else {
			motor[shooter] = 0;
		}
	}
}
